using System;using System.Collections.Generic;using System.Linq;using System.Web;using System.ComponentModel.DataAnnotations;namespace Person.Models{    public class PersonModel    {        public int Id { get; set; }

        [StringLength(20, ErrorMessage = "Your FullName can contain only 20 characters")]        public string FullName { get; set; }        public string Gender { get; set; }        public string Province { get; set; }        [DisplayFormat(DataFormatString = "{0:yyyy-MM-dd}", ApplyFormatInEditMode = true)]        public DateTime DateOfBirth { get; set; }        [Required]        [Display(Name = "Email Address")]        [DataType(DataType.EmailAddress)]        public string Email { get; set; }        public string Password { get; set; }        public string Title { get; set; }        public byte[] Image { get; set; }    }}